mapper.select:
SELECT oo.project_no projectNo, oo.order_stage stage, oo.order_no designerOrderNo, P .create_time projectCreateTime, P .province, P .city, P .region, oo.company_id, P .address_detail address, ComMsg.company_name, contract.contract_number, contract.audit_type, contract.conract_url_pdf, contract.sign_time FROM order_user ou LEFT JOIN designer_order oo ON ou.project_no = oo.project_no LEFT JOIN project P ON oo.project_no = P .project_no LEFT JOIN company_info ComMsg ON oo.company_id = ComMsg.company_id LEFT JOIN pc_order_contract contract ON oo.order_no = contract.order_number WHERE ou.role_code = 'CD' AND ou.user_id =#{designerId} AND oo.status =#{status}
AND oo.order_stage IN
#{state}
AND P.project_no in
#{projectNo}
limit #{pageSize} offset #{pageNum}

SELECT count(*) FROM order_user ou LEFT JOIN designer_order oo ON ou.project_no = oo.project_no LEFT JOIN project P ON oo.project_no = P .project_no LEFT JOIN company_info ComMsg ON oo.company_id = ComMsg.company_id WHERE ou.role_code = 'CD' AND ou.user_id =#{designerId} AND oo.status =#{status}
AND oo.order_stage IN
#{state}
AND P.project_no in
#{projectNo}
