mapper.select:
SELECT

FROM
pc_dealer_brand_info brand
LEFT JOIN company_info info ON brand.company_id = info.company_id
AND brand.agency_code = info.dealer_company_id
LEFT JOIN pc_audit_info audit ON brand.company_id = audit.company_id
AND audit.contract_number = brand.id
WHERE
brand.is_valid = 1
AND info.audit_status = 8

  and brand.company_id = #{companyId}


  and brand.agency_code = #{agencyCode}


  and brand.category_no = #{categoryNo}


  and brand.brand_no = #{brandNo}


  and brand.audit_status = #{status}

ORDER BY
audit.audit_time desc,brand.update_time DESC;

SELECT agency_code, company_id, brand_no, category_no, brand_name, category_name, sum( CASE WHEN audit_status = 0 THEN 1 ELSE 0 END ) AS audit_count, sum( CASE WHEN audit_status = 0 THEN 1 ELSE 0 END ) AS audit_change_count FROM pc_dealer_brand_info WHERE is_valid = 1
and company_id = #{companyId}
and agency_code = #{agencyCode}
GROUP BY brand_no, category_no, agency_code, company_id, brand_name, category_name

SELECT
FROM pc_agency_contract contract LEFT JOIN pc_dealer_brand_info brand ON contract.company_id = brand.company_id AND contract.dealer_company_id = brand.agency_code AND contract.brand_no = brand.brand_no AND contract.category_no = brand.category_no WHERE contract.STATUS = 8 AND brand.is_valid = 1 AND brand.audit_status = 2
and contract.company_id = #{companyId}
