mapper.select:
SELECT , info.company_name FROM pc_dealer_brand_info brand LEFT JOIN company_info info ON brand.company_id = info.company_id AND brand.agency_code = info.dealer_company_id LEFT JOIN ( SELECT audit_type, company_id, audit_persion, audit_status at_status, audit_time, audit_case, audit_account, contract_number, audit_status, audit_level FROM pc_audit_info pc_audit_info WHERE id IN ( SELECT max( id ) FROM pc_audit_info WHERE audit_type = 8 GROUP BY contract_number ) ) audit ON brand.company_id = audit.company_id AND audit.contract_number = brand.id WHERE brand.is_valid = 1 AND info.audit_status = 8
and brand.id = #{brandId}
and brand.company_id = #{companyId}
and brand.agency_code = #{agencyCode}
and brand.brand_no = #{brandNo}
and brand.audit_status = #{status}
ORDER BY audit.audit_time desc,brand.update_time DESC

SELECT agency_code, company_id, brand_no, brand_name, sum( CASE WHEN audit_status in(0,5) THEN 1 ELSE 0 END ) AS audit_count, sum( CASE WHEN audit_status = 5 THEN 1 ELSE 0 END ) AS audit_change_count FROM pc_dealer_brand_info WHERE is_valid = 1
and company_id = #{companyId}
and agency_code = #{agencyCode}
GROUP BY brand_no, agency_code, company_id, brand_name

SELECT
FROM pc_dealer_brand_info brand inner JOIN ( SELECT company_id,dealer_company_id, brand_no, string_agg ( category_name, ',' ) category_name, string_agg ( category_no, ', ' ) category_code FROM pc_agency_contract where status = 8 GROUP BY company_id, brand_no, dealer_company_id ) category ON category.company_id = brand.company_id and brand.brand_no = category.brand_no and brand.agency_code = category.dealer_company_id WHERE brand.is_valid = 1 AND brand.audit_status = 2
and category.company_id = #{companyId}
