mapper.select:
SELECT
FROM pc_dealer_brand_info brand LEFT JOIN company_info info ON brand.company_id = info.company_id AND brand.agency_code = info.dealer_company_id LEFT JOIN pc_audit_info audit ON brand.company_id = audit.company_id AND audit.contract_number = brand.id WHERE brand.is_valid = 1 AND info.audit_status = 8
and brand.company_id = #{companyId}
and brand.agency_code = #{agencyCode}
and brand.category_no = #{categoryNo}
and brand.brand_no = #{brandNo}
ORDER BY brand.update_time DESC

SELECT agency_code, company_id, brand_no, category_no, brand_name, category_name, sum( CASE WHEN audit_status = 0 THEN 1 ELSE 0 END ) AS count FROM pc_dealer_brand_info WHERE AND is_valid = 1
and brand.company_id = #{companyId}
and brand.agency_code = #{agencyCode}
GROUP BY brand_no, category_no, agency_code, company_id, brand_name, category_name
