mapper.select:
SELECT count(1) FROM designer_order o LEFT JOIN construction_order co ON co.project_no = o.project_no
LEFT JOIN project_delay d ON d.project_no = o.project_no LEFT JOIN project_big_scheduling_details b ON b.project_no = o.project_no LEFT JOIN company_info i ON i.company_id = o.company_id LEFT JOIN project P ON P .project_no = o.project_no LEFT JOIN pc_order_contract po ON po.order_number = o.order_no
WHERE o.project_no=#{projectOrderVO.projectNo} AND o.status=#{projectOrderVO.status}

SELECT dor.order_no AS sunOrderNo, dor.status AS status, p.project_no AS orderNo, p.order_source AS orderSource, pr.province_name AS orderAddress, po.contract_number AS contractNo, po.sign_time AS signingTime, po.audit_type AS auditType, po.start_time AS startSign, po.end_time AS endSign FROM designer_order dor LEFT JOIN project p ON p.project_no = dor.project_no LEFT JOIN pc_order_contract po ON po.order_number = dor.order_no LEFT JOIN province pr ON pr.province_code = p.province WHERE dor.company_id = #{designContractVO.companyId} AND dor.status = #{designContractVO.status}
and po.contract_number like CONCAT('%',#{designContractVO.contractNo},'%' )
and dor.order_no like CONCAT('%',#{designContractVO.orderNo},'%' )
and p.order_source like CONCAT('%',#{designContractVO.orderSource},'%' )
and pr.province_name like CONCAT('%',#{designContractVO.orderAddress},'%' )
and po.audit_type like CONCAT('%',#{designContractVO.auditType},'%' )
AND po.sign_time >= #{designContractVO.startSign}
AND po.sign_time <=#{designContractVO.endSign}
and po.audit_type = '1' and po.sign_time > now()
and ( po.audit_type = '0' or po.sign_time < now() )
ORDER BY dor.create_time DESC
limit #{pageSize} offset #{pageNum}
