mapper.select:
SELECT pc_user_info."name", pc_user_info."id", pc_user_info.create_time, pc_user_info.enabled, pc_user_info.email, pc_user_info.phone, pc_user_info.branch_company_id, pc_user_info.city_branch_company_id, pc_user_info.memo, pc_city_branch.city_branch_name, pc_branch_company.company_name, hr_people_entity.people_group, hr_people_entity.people_number, ( SELECT array_to_string( ARRAY ( SELECT pc_system_role.name FROM pc_system_role pc_system_role JOIN pc_system_user_role pc_system_user_role ON pc_system_role.ID = pc_system_user_role.role_id AND pc_system_user_role.user_id = pc_user_info."id" ), ',' ) ) AS roles FROM pc_user_info pc_user_info JOIN hr_people_entity hr_people_entity on pc_user_info.third_id = hr_people_entity.id LEFT JOIN pc_branch_company pc_branch_company on pc_branch_company."branch_company_code" = pc_user_info.branch_company_id LEFT JOIN pc_city_branch pc_city_branch on pc_city_branch."city_branch_code" = pc_user_info.city_branch_company_id WHERE pc_user_info.is_delete = 0
and pc_branch_company.branch_company_code = #{branchCompany}
and pc_city_branch.city_branch_code = #{cityBranch}
and pc_branch_company.id = #{dept}
and (pc_user_info.name like concat(#{name},'%') or pc_user_info.phone like concat(#{name},'%') )
and pc_user_info.enabled = #{state}
order by pc_user_info.create_time desc
