mapper.select:
SELECT ppg.project_no, ppg.project_name, ppg.province, ppg.city, ppg.area, ppg.customer_name, ppg.customer_phone, ppi.house_area, ppi.house_type, ppi.store, ppg.status, ci.company_name, (SELECT user_name FROM pre_project_user_role where project_no = ppg.project_no and role_id = '管家') as stewardName, (SELECT user_name FROM pre_project_user_role where project_no = ppg.project_no and role_id = '项目经理') as projectManagerName FROM pre_project_guide ppg JOIN pre_project_company_set ppcs ON ppg.project_no = ppcs.project_no JOIN pre_project_info ppi ON ppi.project_no = ppg.project_no join company_info ci on ci.company_id = ppcs.company_id
and ppg.is_delete = 0 and ppcs.company_id in
#{listItem}
and ppg.province = #{province}
and ppg.city = #{city}
and ppg.area = #{area}
and ppg.status = #{status}
and ppg.hall = #{hall}
and ppg.customName = #{customName}
and ppg.project_no in
#{listItem}

SELECT to_char(p.create_time, 'YYYY/MM/DD') as dateLine, count(1) as num from pre_project_guide p join pre_project_company_set ppcs on p.project_no = ppcs.project_no where p.is_delete = 0 and p.create_time BETWEEN #{firstDate} AND #{lastDate} and ppcs.company_id in
#{companyItem}
GROUP BY p.create_time order by p.create_time

select ppg.project_no, ppg.project_name, ppg.province, ppg.city, ppg.area, ppg.customer_name, ppg.customer_phone, ppg.status, ppg.project_starttime, ppg.project_endtime, (SELECT user_name FROM pre_project_user_role where project_no = ppg.project_no and role_id = '管家') as stewardName, (SELECT user_name FROM pre_project_user_role where project_no = ppg.project_no and role_id = '项目经理') as projectManagerName
from pre_project_guide ppg join pre_project_user_role ppur on ppg.project_no = ppur.project_no where ppur.user_id = #{userID} and ppg.is_delete = 0 and ppur.is_job = 1 and ppur.is_transfer = 0
and ppg.status = #{status}

select ppg.project_no, ppg.project_name, ppg.province, ppg.city, ppg.area, ppg.customer_name, ppg.customer_phone, ppg.status, ppg.project_starttime, ppg.project_endtime, (SELECT user_name FROM pre_project_user_role where project_no = ppg.project_no and role_id = '管家') as stewardName, (SELECT user_name FROM pre_project_user_role where project_no = ppg.project_no and role_id = '项目经理') as projectManagerName
from pre_project_guide ppg join pre_project_company_set ppcs on ppg.project_no = ppcs.project_no where ppur.user_id = #{userID} and ppg.is_delete = 0 and ppcs.is_delete = 0 and ppcs.company_id = #{companyID}
and ppg.status = #{status}
